    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        this.setExtendedState(this.getExtendedState()|Frame.MAXIMIZED_BOTH);
        fetchh();
        magic.setVisible(false);
        TableModel model = magictable.getModel();
            int selectedRowIndex = magictable.getSelectedRow();
            magix.setText(model.getValueAt(0, 1).toString());
        
            
        Function f = new Function();
        ResultSet res = null;
        res = f.find(magix.getText());
        try{
            if(res.next()){
                id.setText(res.getString("id"));
                String lname = (res.getString("lastname"));
                String fname = (res.getString("firstname"));
                String mname = (res.getString("middlename"));
                String full = lname + ", " +fname + " "+ mname;
                usernamee.setText(full);
                clearmsg.setText(res.getString("status"));
                msg.setText(res.getString("message"));
        }
        }
        catch(Exception e){
        }
        if(clearmsg.getText().equals("You have unreturned book(s)")){
            check.setText("Please check Book Monitor for details");
        }
     
    }//GEN-LAST:event_formWindowOpened

