    public void fetchh(){
        try{
            cn=DriverManager.getConnection("jdbc:mysql://localhost/lms","root","");
            String q = "Select loginno as 'Login No' , username as 'username' from studentlogin order by loginno desc";
            ps = cn.prepareStatement(q);
            res=ps.executeQuery();
            magictable.setModel(DbUtils.resultSetToTableModel(res));
        } 
        catch(Exception e){
        }
    }
    public class Function{
        Connection cn = null;
        ResultSet res = null;
        PreparedStatement ps = null;
        public ResultSet find(String s){
            try{
                cn=DriverManager.getConnection("jdbc:mysql://localhost/lms","root","");
                ps = cn.prepareStatement("Select * from clearance where username = ?");
                ps.setString(1, s);
                res = ps.executeQuery();
                }      
                catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
            return res;
        }
    }
