    private void masterlistMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_masterlistMouseClicked
         DefaultTableModel model = (DefaultTableModel)masterlist.getModel();
        int selectedRowIndex = masterlist.getSelectedRow();
        
        try{
            search_bookno.setText(model.getValueAt(selectedRowIndex, 0).toString());
            
            }
        catch(Exception e){
            
        }
      
    }//GEN-LAST:event_masterlistMouseClicked
   public class Function{
        Connection cn = null;
        ResultSet res = null;
        PreparedStatement ps = null;
        public ResultSet find(String s){
            try{
                cn=DriverManager.getConnection("jdbc:mysql://localhost/lms","root","");
                ps = cn.prepareStatement("Select * from masterlist where booknumber = ?");
                ps.setString(1, s);
                res = ps.executeQuery();
                }      
                catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
            return res;
        }
    }
