    private void sortbyActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sortbyActionPerformed
        if(sortby.getSelectedItem() == "TITLE"){
            sort = "title";

        }
        if(sortby.getSelectedItem() == "BOOKNO"){

            sort = "booknumber";
   
        }
        fetch();
        
    }//GEN-LAST:event_sortbyActionPerformed

    private void searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchActionPerformed
        Function f = new Function();
        ResultSet res = null;
        res = f.find(search_bookno.getText());
        try{
            if(res.next()){
                bookno.setText(res.getString("booknumber"));
                title.setText(res.getString("title"));
                title1.setText(res.getString("subtitle"));
                author.setText(res.getString("author"));
                publisher.setText(res.getString("publisher"));
                language.setText(res.getString("language"));
            }
            else{
                errormsg.setText("*BookNo does not Exist.");
            }
            if(bookno.getText().isEmpty()){
                errormsg.setText("*BookNo does not Exist.");
            }else{
                errormsg.setText("");
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_searchActionPerformed

