    private void masterlistMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_masterlistMouseClicked
 
        DefaultTableModel model = (DefaultTableModel)masterlist.getModel();
        int selectedRowIndex = masterlist.getSelectedRow();
        
        try{
        bookno.setText(model.getValueAt(selectedRowIndex, 0).toString());

        }
        catch(Exception e){
            
        }
         Function f = new Function();
        ResultSet res = null;
        res = f.find(bookno.getText());
        try{
            if(res.next()){
                admin_viewtitle.setText(res.getString("title"));
                admin_viewtitle.setEnabled(false);
                admin_editsubtitle.setText(res.getString("subtitle"));
                admin_editsubtitle.setEnabled(false);
                admin_editauthor.setText(res.getString("author"));
                admin_editauthor.setEnabled(false);
                admin_editpublisher.setText(res.getString("publisher"));
                admin_editpublisher.setEnabled(false);
                admin_editlang.setSelectedItem(res.getString("language"));
                admin_editlang.setEnabled(false);
            }
            else{
                JOptionPane.showMessageDialog(null, "Select one you wish to view details");
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
      
    }//GEN-LAST:event_masterlistMouseClicked
   public class Function{
        Connection cn = null;
        ResultSet res = null;
        PreparedStatement ps = null;
        public ResultSet find(String s){
            try{
                cn=DriverManager.getConnection("jdbc:mysql://localhost/lms","root","");
                ps = cn.prepareStatement("Select * from masterlist where booknumber = ?");
                ps.setString(1, s);
                res = ps.executeQuery();
                }      
                catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
            return res;
        }
    }
