    private void admin_editsaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_admin_editsaveActionPerformed
        cn=sql.myConnection();
        String name = admin_editauthor.getText().toString();
        try{
          
            if(admin_viewtitle.getText().isEmpty()){
                errormsg.setText("Please Fill-up this Information.");
                errortitle.setText("*");
            } else{
                
                errortitle.setText("");
            }
             if(admin_editsubtitle.getText().isEmpty()){
                errormsg.setText("Please Fill-up this Information.");
                errorsubtitle.setText("*");
            } else{
                
                errorsubtitle.setText("");
            }
              if(admin_editauthor.getText().isEmpty()){
                errormsg.setText("Please Fill-up this Information.");
                errorauthor.setText("*");
              }  
             else{
                errorauthor.setText("");
            } 

              
               if(admin_editpublisher.getText().isEmpty()){
                errormsg.setText("Please Fill-up this Information.");
                errorpub.setText("*");
            } else{
                
                errorpub.setText("");
            }
               if(admin_editlang.getSelectedItem() == " "){
                errormsg.setText("Please Fill-up this information.");
                errorlang.setText("*");
            } else{
                
                errorlang.setText("");
            }
               if(errortitle.getText().isEmpty() & errorsubtitle.getText().isEmpty() & errorauthor.getText().isEmpty()& errorpub.getText().isEmpty() & errorlang.getText().isEmpty()){
                   errormsg.setText("");
               }
                if(!name.matches("^[a-zA-Z ]+")){
                   errormsg.setText("Please Fill-up with correct information.");
                   errorauthor.setText("*");
               }
                 if(errormsg.getText().isEmpty()){
                     String ins="UPDATE masterlist set title=?, subtitle=?, author=?, publisher=?, language=? where booknumber="+bookno.getText()+"";
                     ps=cn.prepareStatement(ins);            
                     ps.setString(1, admin_viewtitle.getText());           
                     ps.setString(2, admin_editsubtitle.getText());
                     ps.setString(3, admin_editauthor.getText());
                     ps.setString(4, admin_editpublisher.getText());
                     String lang=admin_editlang.getSelectedItem().toString();
                     ps.setString(5, lang);
                     ps.executeUpdate();
 
                     JOptionPane.showMessageDialog(null, "Successful!");
                     bookno.setText("");
                     admin_viewtitle.setText("");
                     admin_editsubtitle.setText("");
                     admin_editauthor.setText("");
                     admin_editpublisher.setText("");
                     admin_editlang.setSelectedItem(" ");
                 }
        
        }catch(Exception e){
            
        }
    }//GEN-LAST:event_admin_editsaveActionPerformed

    private void sortbyActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sortbyActionPerformed
        if(sortby.getSelectedItem() == "TITLE"){
            sort = "title";
            bookno.setText("");
            admin_viewtitle.setText("");
            admin_editsubtitle.setText("");
            admin_editauthor.setText("");
            admin_editpublisher.setText("");
            admin_editlang.setSelectedItem(" ");
        }
        if(sortby.getSelectedItem() == "BOOKNO"){
            bookno.setText("");
            sort = "booknumber";
            admin_viewtitle.setText("");
            admin_editsubtitle.setText("");
            admin_editauthor.setText("");
            admin_editpublisher.setText("");
            admin_editlang.setSelectedItem(" ");
        }
        fetch();
        
    }//GEN-LAST:event_sortbyActionPerformed

