    public void fetch(){
        try{
            cn=DriverManager.getConnection("jdbc:mysql://localhost/lms","root","");
            String q = "Select booknumber as 'BookNo' , title as 'Title' from masterlist order by " + sort + " asc";
            
            ps = cn.prepareStatement(q);
            res=ps.executeQuery();

            masterlist.setModel(DbUtils.resultSetToTableModel(res));
        } 
        catch(Exception e){
           
        }
    }
 
